mapper.select:
select em.emo_id as emoId, em.emo_code as emoCode, em.emo_name as emoName, em.company, em.emo_date as emoDate, MAX(CASE WHEN ei.image_seq = 1 THEN ei.save_name ELSE NULL END) as newImage1, MAX(CASE WHEN ei.image_seq = 2 THEN ei.save_name ELSE NULL END) as newImage2, MAX(CASE WHEN ei.image_seq = 3 THEN ei.save_name ELSE NULL END) as newImage3, MAX(CASE WHEN ei.image_seq = 4 THEN ei.save_name ELSE NULL END) as newImage4, el.like_id as likeId, lc.like_count as likeCount from emo_mst em left outer join emo_image ei on(em.emo_code = ei.emo_code) left outer join emo_like el on (em.emo_id = el.emo_id AND el.user_id = #{userId}) left outer join ( select emo_id, COUNT(emo_id) as like_count from emo_like group by emo_id ) lc on (lc.emo_id = em.emo_id) where 1=1
and (emo_name like concat('%', #{searchValue}, '%') or company like concat('%', #{searchValue}, '%'))
group by em.emo_id, em.emo_code, em.emo_name, em.company, em.emo_date, el.like_id, lc.like_count order by em.emo_date desc limit #{index}, #{count}
